import axios from "axios";
import React, {useState, useEffect, useCallback }from "react";
import { Helmet } from 'react-helmet';
import CocktailsDataService from "../../services/cocktails.js"

import { Title, Wrapper } from './home.style'

const Home = props => {
    const [cocktail, setCocktail] = useState("");

    function retrieveCocktails() {
        const url = "http://localhost:5000/api/v1/data"
        axios.get(url).then(res => {
            const data = res.data.cocktails;
            const cockNum = Math.floor(Math.random() * data.length);
            const ranCock = data[cockNum];

            setCocktail(ranCock.cocktail);
        });
    }

    useEffect(() => {
        retrieveCocktails();
    }, []);

    const onButtonClick = useCallback(event => {
        retrieveCocktails();
    }, []);

    return (
        <div className="App">
            <Helmet>
                <style>{'body {background-color: orange; }'}</style>
            </Helmet>
            <Wrapper>
                <Title>
                    Cocktail of the Day                
                </Title>
            </Wrapper>
            <button onClick={onButtonClick} />;
        </div>

    )

}

export default Home;
