import axios from "axios";
import React, {useState, useEffect, useCallback }from "react";
import { Helmet } from 'react-helmet';
import CocktailsDataService from "../../services/cocktails.js"

import { Title, Wrapper } from './home.style'

const Home = props => {
    const [cocktails, setCocktails] = useState([]);
    const [searchNums, setSearchNums] = useState('');

    useEffect(() => {
        retrieveNums();
    }, [])

    const findCocktails = (query, by) => {
        query = Math.floor(Math.random() * 100);
        CocktailsDataService.find(query, by).then(response => {
            console.log(response.data);
            setCocktails(response.data.cocktails);
        })
        .catch(e => {
            console.log(e);
        })
    }

    const findByNums = () => {
        findCocktails(searchNums, "Num");
    }

    const retrieveNums = () => {
        CocktailsDataService.getNums()
        .then(response => {
            console.log(response.data);
            setSearchNums(response.data)
        })
    }

    return (
        <div className="App">
            <Helmet>
                <style>{'body {background-color: orange; }'}</style>
            </Helmet>
            <Wrapper>
                <Title>
                    Cocktail of the Day                
                </Title>
            </Wrapper>
            <div>
                <button
                    className="btn btn-outline-secondary"
                    type="button"
                    onClick={findByNums}>Refresh </button>  
                    {cocktails.map((cocktail) => {
                    return (
                    <h5>{cocktail.Cocktail}</h5>
                    )
                })}
            </div>
        </div>

    )

}

export default Home;
