import React, {useState, useEffect }from "react";
import { Helmet } from 'react-helmet';
import CocktailsDataService from "../../services/cocktails.js"

import { Title, Wrapper } from './home.style'

const randomize = () => {
    const rand = Math.floor((Math.random() * 100)) 
    return rand;
}

const Home = () => {
    const [cocktails, setCocktails] = useState([]);
    const [searchNums, setSearchNums] = useState('')

    useEffect(() => {
        retrieveNums();
    }, [])

    const onChangeSearchNums = e => {
        const searchNums = e.target.value;
        setSearchNums(searchNums);
    }

    const find = (query, by) => {
        CocktailsDataService.find(query, by).then(response => {
            console.log(response.data.cocktails);
            setCocktails(response.data.cocktails);
        })
        .catch(e => {
            console.log(e);
        })
    }

    const findByNums = () => {
        const searchNums = Math.floor(Math.random() * 100) 
        find(1, "Image");
    }


    const retrieveNums = () => {
        CocktailsDataService.getNums()
        .then(response => {
            console.log(response.data);
            setSearchNums(response.data)
        })
    }

    return (
        <div className="App">
            <Helmet>
                <style>{'body {background-color: orange; }'}</style>
            </Helmet>
            <Wrapper>
                <Title>
                    Cocktail of the Day                
                </Title>
            </Wrapper>
                <button
                    className="btn btn-outline-secondary"
                    type="button"
                    onClick={findByNums}>Refresh </button>  
                    {cocktails.map((cocktail) => {
                    return (
                    <h5>{cocktail.Cocktail}</h5>
                    )
                })}

        </div>

    )

}

export default Home;
