import React, {useState, useEffect }from "react";
import { Helmet } from 'react-helmet';

import { Title, Wrapper } from './home.style'
const useFetch = (url) => {
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(async () => {
        const response = await fetch(url);
        const data = await response.json();
        const [item] = data.cocktails;
        setData(item);
        setLoading(false);
    }, []);

    return {data, loading}
}
const Home = () => {
    const[count, setCount] = useState(0);
    const {loading, setLoading} = useFetch("http://localhost:5000/api/v1/data")

    return (
        <div className="App">
            <Helmet>
                <style>{'body {background-color: orange; }'}</style>
            </Helmet>
            <Wrapper>
                <Title>
                    Cocktail of the Day                
                </Title>
            </Wrapper>
            <p>You clicked {count} times</p>
            <button onClick={() => setCount(count + 1)}>Click</button>
            {loading ? <div>...loading</div> : <div>{data.Cocktail}</div>}
        </div>

    )

}

export default Home;
