import React, {useState, useEffect }from "react";
import CocktailsDataService from "../../services/cocktails.js"

const SearchBar = props => {
    const [Ingredients, setIngredients] = useState([])
    const [searchIngredients, setSearchIngredients] = useState("");

    useEffect(() => {
        retrieveIngredients();
    }, []);

    const onChangeSearchIngredients = e => {
        const searchIngredients = e.target.value;
        setSearchIngredients(searchIngredients);
    }

    const retrieveIngredients = () => {
        CocktailsDataService.getIngredients()
            .then(response => {
                console.log(response.data);
                setIngredients(response.data.Ingredients);
            })
            .catch(e => {
                console.log(e);
            });
    }

    const find = (query, by) => {
        CocktailsDataService.find(query, by)
        .then(response => {
            console.log(response.data);
            setIngredients(response.data.Ingredients);
        })
        .catch(e => {
            console.log(e);
        });
    };

    const findByIngredients = () => {
        find(searchIngredients, "Cocktail")
    }

    return(
        <div>
            <div className="row pb-1">
                <div className="input-group col-lg-4">
                    <input
                    type="text"
                    className="form-control"
                    placeholder="Search by ingredients"
                    value={searchIngredients}
                    onChange={onChangeSearchIngredients} />
                    <div className="input-group-append">
                        <button
                        className="btn btn-outline-secondary"
                        type="button"
                        onClick={findByIngredients}
                        >
                            Search
                        </button>
                    </div>
                </div>
                <div className="row">
                    {Ingredients.map((ingredient) => {
                        return (
                        <div classNBame="row">
                                <div className="col-lg-4 pb-1">
                                    <div className="card-body">
                                        <h5 className="card-title">{ingredient.cocktails}</h5>
                                    </div>
                                </div>
                        </div>
                        );
                    })};
                </div>
            </div>
        </div>
    );
};

export default SearchBar
