import axios from "axios";
import React, {useState, useEffect }from "react";
import { Helmet } from 'react-helmet';
import CocktailsDataService from "../../services/cocktails.js"

import { Title, Wrapper } from './home.style'

const Home = props => {
    const [cocktails, setCocktails] = useState([]);
    const [searchNums, setSearchNums] = useState('');

    useEffect(async () => {
        findByNums();

        // retrieveNums();
    }, [])

    const find = (query, by) => {
        CocktailsDataService.find(query, by)
        .then(response => {
            console.log(response.data);
            const [item] = response.data.cocktails;
            setCocktails(item);
        })
        .catch(e => {
            console.log(e);
        })
    }

    const findByNums = () => {
        const rand = Math.floor(Math.random() * 100);
        console.log(rand);
        find(rand, "Num");
    }


    // const retrieveNums = () => {
    //     CocktailsDataService.()
    //     .then(response => {
    //         console.log(response.data);
    //         setSearchNums(response.data)
    //     })
    // }

    return (
        <div className="App">
            <Helmet>
                <style>{'body {background-color: orange; }'}</style>
            </Helmet>
            <Wrapper>
                <Title>
                    Cocktail of the Day                
                </Title>
            </Wrapper>
            <div>
                <button
                    className="btn btn-outline-secondary"
                    type="button"
                    onClick={findByNums}>Refresh </button>  
                    {cocktails.map((cocktail) => {
                    return (
                    <h5>{cocktail.Cocktail}</h5>
                    )
                })}
            </div>
        </div>

    )

}

export default Home;
