import React, {useState, useEffect }from "react";
import CocktailsDataService from "../services/cocktails.js"
import cocktails from "../services/cocktails.js";

const SearchBar = props => {
    const [ingredients, setIngredients] = useState([])
    const [searchIngredients, setSearchIngredients] = useState("");

    useEffect(() => {
        retrieveIngredients();

    }, []);

    const onChangeSearchIngredients = e => {
        const searchIng = e.target.value;
        setSearchIngredients(searchIngredients);
    }

    const retrieveIngredients = () => {
        CocktailsDataService.getIngredients()
            .then([response => {
                console.log(response(data));
                setIngredients(["All Ingredients"].concat(response.data));
            }])
            .catch(e => {
                console.log(e);
            });
    }

    const find = (query, by) => {
        CocktailsDataService.find(query, by)
        .then(response => {
            console.log(response.data);
            setIngredients(response.data.cocktails);
        })
        .catch(e => {
            console.log(e);
        });
    };

    const findByIngredients = () => {
        find(searchIngredients, "Ingredients")
    }

    return(
        <div>
            <div className="row pb-1">
                <div className="input-group col-lg-4">
                    <input
                    type="text"
                    className="form-control"
                    placeholde="Search by name"
                    value={searchIngredients}
                    onChange={onChangeSearchIngredients} />
                    <div className="input-group-append">
                        <button
                        className="btn btn-outline-secondary"
                        type="button"
                        onClick={findByIngredients}
                        >
                            Search by ingredients
                        </button>
                    </div>
                </div>
                <div className="input-group col-lg-4">
                    <select onChange={onChangeSearchIngredients}>
                        {cocktails.map(cocktails => {
                            return(
                                <option value={cocktail}> {cocktail.substr(0,20)}</option>
                            )
                        })}
                    </select>
                    <div className="input-group-append">
                        <button
                        className="btn btn-outline-secondary"
                        type="button"
                        onClick={findByIngredients}
                        >
                            Search by ingredients
                        </button>
                    </div>
                </div>
            </div>
        </div>
    )
}




